mapper.insert:
select seq_subject.nextval from dual
insert into assignment ( assignment_no, subject_no, assignment_title, assignment_contents, writer, originalfile, savedfile
) values ( #{assignment_no}, #{subject_no}, #{assignment_title}, #{assignment_contents}, #{writer}, #{originalfile}, #{savedfile} )

mapper.update:
update assignment


		assignment_title = #{assignment_title},

		assignment_contents = #{assignment_contents},
		originalfile = #{originalfile},
		savedfile = #{savedfile}

where assignment_no = #{assignment_no}
