mapper.select:
select * from attendance

	member_id = #{member_id}

		and lecture_no = #{lecture_no}

mapper.update:
update attendance

	accumulate_time = #{accumulate_time},

		attend_check = #{attend_check}


where attendance_no = #{attendance_no}

update attendance

	accumulate_time = #{accumulate_time},
	video_point = #{video_point}

where attendance_no = #{attendance_no}
