mapper.select:
select member_id
from member
where
	member_name = #{member_name}
	and member_email = #{member_email}

mapper.update:
update member
member_password = #{member_password}, member_name = #{member_name}, member_email = #{member_email}, password_question = #{password_question}, password_answer = #{password_answer}
where member_no = #{member_no}
